import { useState } from "react";
import axios from "axios";
import React from "react";
import "./style.css";
export default function C1() 
{
  const le = 
  {
    float:'right'
   };

   const [name, setName] = useState("")
   const [task, setTask] = useState("")



  function hi(){
    axios.post("http://localhost:3000/task/" ,{ name:name,task: task})
    .then((data) => {
      console.log(data);
              setName("")
              setTask("")
       });
  }


  //  function handleClick(){
  //     axios.get("http://localhost:3000/tasks/").then((response) => {
  //     });
  //   }

    return (

     <div className="bg" >
      <nav class="navbar">
            <ul>
                <li><a href="#home">TaskMap</a></li>
                <li style={le}><button onClick={hi} class="button">View </button></li>
            </ul>
        </nav>

        <p>
          <img src="/image.png" alt="vomputer"></img>
        <label>Name : <input className="size" type="text" id="name" placeholder="Enter your name" value={name}
            onChange={(e) => setName(e.target.value)}/>
        </label>
        <br></br> <br></br> 

        <label className="la">Task : <input className="size" type="text" id ="task" placeholder="Enter your task"
        value={task}
        onChange={(e) => setTask(e.target.value)}/></label>
        <br></br> <br></br> 

        <button onClick={hi}  className="subsize">submit </button>
        </p>





        {/* <p>hi</p>
       <button onClick={handleClick} >Click</button> */}

      </div>

    );

}
