import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import "./style.css";
export default function C2() 
{
    const le = 
    {
      float:'right'
     };

    const [taskData,setData] = useState([])

    useEffect(()=>{
        axios.get("http://localhost:3000/tasks/")
         .then((response) => {
          setData(response.data)
           });
      },[])


    return (
        <div>
            <nav class="navbar">
            <ul>
                <li><a href="#home">TaskMap</a></li>
                <li style={le}><button class="button">View </button></li>
            </ul>
        </nav> 


            <table>
          <thead>
              <tr>
                <th>Name</th>
                <th>Task</th>
                <th>Completed</th>
                <th>Delete</th>
              </tr>
          </thead>
          <tbody>
          {/* filter(completed => completed.includes(false)) */}
          {taskData.map((data)=>(
              <tr>
                <td>{data.name}</td>
                <td>{data.task}</td>
                <td className="buts"><button className="butss" onClick={ () =>
                axios.patch(`http://localhost:3000/task/${data.id}`)
                }>
                  Update</button></td>

                <td className="buts"><button className="butss" onClick={ () =>
                axios.delete(`http://localhost:3000/delete/${data.id}`)
                }>  
                  Delete</button></td>
              </tr>
          ))}

          </tbody>

          </table>
        </div>
    );

}
