   };

   const [name, setName] = useState("")
   const [task, setTask] = useState("")






  function hi(){


    const add = (name, setName,task, setTask ) => {

      axios
          .post(`http://localhost:3000/task/`, { name },{task})
          .then((data) => {
              console.log(data);
              setName("")
              setTask("")
          })
          .catch((err) => console.log(err))

       }
  }


  //  function handleClick(){
